public class test2 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int depositAmount;
		int n = 500;
		 depositAmount=5000;
		 System.out.println("저축액=" + depositAmount);
		 System.out.println("저축액=" + depositAmount+" " +"저축횟수="+ n);
		 
		 /*리터럴*/
		 System.out.println("저축액=" +5000 +"  " + "저축횟수="+ 500); /*문자열 리터럴과 정수 리터럴*/
		 System.out.println("저축액=" +5000.0 +"   " + "저축횟수="+ 500.0); /* 실수 리터럴*/
		 System.out.println("저축액=" +depositAmount +"저축횟수="+ n  + (depositAmount==n)); /*논리 리터럴*/
		 System.out.println("저축액=" +depositAmount +"저축횟수="+ n  + (depositAmount!=n)); /*논리 리터럴*/
		 
		 char gender = 'F';
		 System.out.println("저축액=" +5000 +"  " + "저축횟수="+ 500 +gender ); /*문자열 리터럴과 정수 리터럴*/
		 
		 
	}

}
